#!/bin/bash
set -u
set -e

while [ 1 ]; do
  for i in {1..100}
  do
    curl -X POST -H "Content-Type: application/json" --data '{ "jsonrpc":"2.0", "id":1, "method":"eth_getTransactionCount", "params":[ "0xed9d02e382b34818e88b88a309c7fe71e65f419d", "pending" ] }' http://localhost:22000 

    curl -X POST -H "Content-Type: application/json" --data '{ "jsonrpc":"2.0", "id":1, "method":"eth_estimateGas", "params":[ {"data":""} ] }' http://localhost:22000 

    ./runscript.sh "public-contract.js"
  done
  echo "PAUSE"
  sleep 5
done
